(function() {
  window.initForms = function() {
    $('.notice, .error').each(function() {
      return $(this).height($(this).height());
    });
    $('.notice, .error').click(function() {
      return $(this).addClass('hidden');
    });
    $('.input input, .input textarea, .input select').focus(function() {
      return $(this).parents('.input').addClass('focus');
    });
    $('.input input, .input textarea, .input select').blur(function() {
      return $(this).parents('.input').removeClass('focus');
    });
    $('.input select').each(function() {
      return $(this).wrap('<div class="select"/>');
    });
    $('output[for]').each(function() {
      var input, output;
      output = $(this);
      input = $("#" + (output.attr('for')));
      input.on('input', function() {
        return output.val(input.val());
      });
      return output.val(input.val());
    });
    $('select:not(.date):not(.time):not(.datetime)').chosen({
      width: '100%'
    });
    $('select[multiple]').parents('.select').addClass('multiple');
    return $('input[type=file]').change(function(e) {
      var container, input, value;
      input = $(this);
      value = input.val();
      container = input.parents('.input');
      return container.find('.label').removeClass('placeholder').text(value);
    });
  };

}).call(this);

