/* +----------------------------------------------------------------------------+
   + bracket-matcher                                                        [1] +
   +----------------------------------------------------------------------------+ */
.bracket-matcher.region {
  border-bottom: 2px solid #85c1d3;
  border-radius: 1px;
}
/* +------------------------------------------------------------------------[1]-+ */
/* +----------------------------------------------------------------------------+
   + color-picker                                                           [2] +
   +----------------------------------------------------------------------------+ */
.ColorPicker .ColorPicker-body {
  background-color: #1f1f23;
}
.ColorPicker .ColorPicker-color-text {
  font-size: 1.5em;
  color: #dedede;
}
.ColorPicker .ColorPicker-format {
  background-color: #1f1f23;
}
.ColorPicker .ColorPicker-format .ColorPicker-format-button {
  background-color: #5b81af;
  color: #dedede;
  text-shadow: none;
  font-size: 1em;
}
.ColorPicker .ColorPicker-format .ColorPicker-format-button.is--active {
  background-color: #6c8eb7;
  font-weight: bold;
}
/* +------------------------------------------------------------------------[2]-+ */
/* +----------------------------------------------------------------------------+
   + hightlight-selected                                                    [3] +
   +----------------------------------------------------------------------------+ */
.highlight-selected .region {
  border-color: #85c1d3 !important;
}
/* +------------------------------------------------------------------------[3]-+ */
/* +----------------------------------------------------------------------------+
   + markdown-preview                                                       [4] +
   +----------------------------------------------------------------------------+ */
.markdown-preview {
  /* +----------------------------------------------------------------------------+
     + markdown-preview - Atom Editor                                       [4.0] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[4.0]-+ */
  /* +----------------------------------------------------------------------------+
     + markdown-preview - Blockquote                                        [4.1] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[4.1]-+ */
  /* +----------------------------------------------------------------------------+
     + markdown-preview - Code                                              [4.2] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[4.2]-+ */
}
.markdown-preview atom-text-editor {
  background-color: #1f1f23;
}
.markdown-preview blockquote {
  border-left-color: #dedede;
}
.markdown-preview code {
  color: #85c1d3;
}
.markdown-preview code:hover {
  background-color: #4f4f59;
}
/* +------------------------------------------------------------------------[4]-+ */
/* +----------------------------------------------------------------------------+
   + minimap-highlight-selected                                             [5] +
