  /* +----------------------------------------------------------------------------+
     + Constant                                                               [2] +
     +----------------------------------------------------------------------------+ */
  /* +------------------------------------------------------------------------[2]-+ */
  /* +----------------------------------------------------------------------------+
     + Entity                                                                 [3] +
     +----------------------------------------------------------------------------+ */
  /* +------------------------------------------------------------------------[3]-+ */
  /* +----------------------------------------------------------------------------+
     + Keyword                                                                [4] +
     +----------------------------------------------------------------------------+ */
