}
.source.c .punctuation.section.block.begin,
.source.c .punctuation.section.parens.begin,
.source.c .punctuation.section.block.end,
.source.c .punctuation.section.parens.end {
  color: #eceeef;
}
.source.c .punctuation.separator.continuation {
  color: #fdce84;
}
.source.c .punctuation.separator.parameters {
  color: #eceeef;
}
.source.c .support.type.sys-types {
  color: #8fcec8;
}
.source.c .support.function.any-method,
.source.c .support.function.C99 {
  color: #c9cc78;
}
.source.c .storage.type {
  color: #8fcec8;
}
.source.c .string {
  color: #eceeef;
}
.source.c .variable.parameter {
  color: #dedede;
}
