.icon-info:before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 12px;
  width: 16px;
  height: 16px;
  content: "\f026";
}
.icon-info .content {
  display: none;
}
.icon-info:hover {
  color: #ffffff;
  cursor: pointer;
  z-index: 1000;
  background-color: #1e1e1e;
  color: #a8a8a8;
}
.icon-info:hover .content {
  display: block;
  position: relative;
  float: left;
  padding: 5px 5px 5px 5px;
}
.icon-info:hover .content .bold {
  font-weight: bold;
  padding: 0 0 0 2px;
}
.icon-info:hover .content .info {
  display: flex;
  font-family: monospace;
}
.icon-info:hover .content .info .col {
  padding: 0 2px 0 2px;
}
