css:
.gutter .php-debug-gutter {
  height: 7px !important;
  width: 7px;
  display: block;
  margin-left: 2px;
  margin-top: 6px;
  border-radius: 50%;
}
.gutter .php-debug-gutter.highlight {
  background-color: #0088ff;
  color: white;
}
.gutter > .line-numbers > .php-debug-breakpoint,
.gutter > .line-numbers > div > .php-debug-breakpoint,
.gutter > .line-numbers > .tile > .php-debug-breakpoint {
  color: #fff;
  background-color: #0F0 !important;
}
.debug-break-break {
  background-color: rgba(32, 182, 132, 0.25) !important;
}
.debug-break-generic {
  background-color: rgba(32, 182, 132, 0.25) !important;
}
.debug-break-error {
  background-color: rgba(217, 38, 38, 0.15) !important;
}
.php-debug {
  overflow: auto;
}
.php-debug-unified-view {
  margin: 10px;
}
.php-debug-status-view {
  display: inline-block;
  color: rgba(158, 158, 178, 0.6);
  border: 1px solid #1b1b22;
  background: rgba(59, 59, 74, 0.33);
  cursor: pointer;
  vertical-align: middle;
  position: relative;
  padding: 0 .6em;
  line-height: 1.8em;
  margin-right: 0.6em;
}
.php-debug-status-view:active {
  background: transparent;
}
.php-debug-status-view.active {
  color: #d7d7df;
  background: #3b3b4a;
}
