css:
@-webkit-keyframes neon-selection {
  0% {
    box-shadow: 0 0 3px 0px #10b1f1, 0 0 3px 0px #10b1f1 inset;
  }
  100% {
    box-shadow: 0 0 4px 0px #4bd5ff, 0 0 4px 0px #4bd5ff inset;
  }
}
.editor .selection .region,
atom-text-editor .selection .region,
:host .selection .region {
  border: 1px solid #61d2ff;
  background: rgba(70, 203, 255, 0.1) !important;
  box-shadow: 0 0 4px 0px #4bd5ff, 0 0 4px 0px #4bd5ff inset;
  -webkit-animation: neon-selection 0.1s linear infinite;
  opacity: 1 !important;
}
